import React, { useEffect, Fragment } from 'react';
import { useLocation } from 'react-router-dom';
import { stringify } from 'query-string';

import useFetch from 'hooks/useFetch';
import Feed from 'components/feed';
import Pagination from 'components/pagination';
import { getPaginator, limit } from 'utils';
import PopularTags from 'components/popularTags';
import Loading from 'components/loading'
import ErrorMessage from 'components/errorMessage'
import FeedToggle from 'components/feedToggle';

const GlobalFeed = () => {
    const {search} = useLocation();
    const {offset, currentPage} = getPaginator(search);
    const stringifielParams = stringify({
        limit,
        offset
    });
    const apiUrl = `/articles?${stringifielParams}`;
    const [{response, isLoading, error}, doFetch] = useFetch(apiUrl);

    useEffect(() => {
        doFetch();
    }, [doFetch, currentPage]);

    return (
        <div className='home-page'>
            <div className='banner'>
                <div className='container'>
                    <h1>Medium clone</h1>
                    <p>Aplace to shareknowledge</p>
                </div>
            </div>
            <div className='container page'>
                <div className='row'>
                    <div className='col-md-9'>
                        <FeedToggle />
                        {isLoading && <Loading />}
                        {error && <ErrorMessage />}
                        {!isLoading && response && (
                            <Fragment>
                                <Feed articles={response.articles} />
                                <Pagination total={response.articlesCount} limit={limit} url="/" currentPage={currentPage} />
                            </Fragment>
                        )}
                    </div>
                    <div className='col-md-3'>
                        <PopularTags />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default GlobalFeed;