import React, { useEffect } from 'react';
import { NavLink, useParams, useLocation } from 'react-router-dom';

import useFetch from 'hooks/useFetch';
import UserArticles from 'pages/userProfile/components/userArticles.js'

const UserProfile = () => {
    const { slug } = useParams();
    const location = useLocation();
    const isFavorites = location.pathname.includes('favorites');
    const apiUrl = `/profiles/${slug}`;
    const [{ response, isLoading, error }, doFetch] = useFetch(apiUrl);

    useEffect(() => {
        doFetch();
    }, [doFetch, slug]);

    useEffect(() => {
        if (!response) {
            return;
        }
    }, [response]);

    if (!response) {
        return null;
    }
    
    return (
        <div className='profile-page'>
            <div className='user-info'>
                <div className='container'>
                    <div className='row'>
                        <div className='col-xs-12 col-md-10 offset-md-1'>
                            <img className='user-img' src={response.profile.image} alt={response.profile.username} />
                            <h4>{response.profile.username}</h4>
                            <p>{response.profile.bio}</p>
                        </div>
                    </div>
                </div>
            </div>
            <div className='container'>
                <div className='row'>
                    <div className='col-xs-12'>
                        <div className='article-toggle'>
                            <ul className='nav nav-pills outline-active'>
                                <li className='nav-item'>
                                    <NavLink to={`/profiles/${response.profile.username}`} className='nav-link'>
                                        My Posts
                                    </NavLink>
                                </li>
                                <li className='nav-item'>
                                    <NavLink to={`/profiles/${response.profile.username}/favorites`} className='nav-link'>
                                        Favorites Posts
                                    </NavLink>
                                </li>
                            </ul>
                        </div>
                        {<UserArticles
                            username={response.profile.username}
                            location={location}
                            isFavorites={isFavorites}
                            url={location.pathname}
                        />}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default UserProfile;